document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("contact-form");e.addEventListener("submit",function(t){t.preventDefault();const n=document.getElementById("name").value,s=document.getElementById("email").value,o=document.getElementById("message").value,i={name:n,email:s,message:o};fetch("https://semiconware.com/api/contact",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(i)}).then(e=>{if(!e.ok)throw new Error("Network response was not ok");return e.json()}).then(t=>{e.innerHTML='<div class="thank-you-message"><h2>Thank you!</h2><p>Your message has been sent successfully. We will get back to you soon.</p></div>'}).catch(t=>{console.error("Error:",t);const n=document.createElement("div");n.className="alert alert-danger",n.textContent="There was an error sending your message. Please try again.",e.prepend(n)})})})